// ===========================================
// PRODUCTS PAGE COMPONENT - IMPROVED
// ===========================================
import React from 'react';
import { Search } from 'lucide-react';
import ProductCard from '../components/ProductCard';
import { searchProducts } from '../data/products';

const ProductsPage = ({ 
  searchTerm, 
  setSearchTerm, 
  selectedCategory, 
  setSelectedCategory,
  addToCart,
  setSelectedProduct 
}) => {
  const filteredProducts = searchProducts(searchTerm, selectedCategory);

  return (
    <div className="min-h-screen bg-gray-50 py-4 md:py-8">
      <div className="container mx-auto px-4">
        {/* Search and Filter Section */}
        <div className="bg-white p-4 md:p-6 rounded-xl shadow-lg mb-6 md:mb-8">
          <div className="flex flex-col gap-4">
            {/* Search Bar */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Cari action figure..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-300"
              />
            </div>
            
            {/* Category Filter */}
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-300 bg-white"
            >
              <option value="all">Semua Kategori</option>
              <option value="marvel">Marvel</option>
              <option value="dc">DC</option>
              <option value="anime">Anime</option>
              <option value="kamen rider">Kamen Rider</option>
              <option value="super sentai">Super Sentai</option>
            </select>
          </div>
        </div>

        {/* Results Info */}
        <div className="mb-6">
          <h2 className="text-xl md:text-2xl font-bold text-gray-900">
            {selectedCategory === 'all' ? 'Semua Produk' : selectedCategory.charAt(0).toUpperCase() + selectedCategory.slice(1)}
          </h2>
          <p className="text-sm md:text-base text-gray-600">
            Menampilkan {filteredProducts.length} produk
            {searchTerm && ` untuk "${searchTerm}"`}
          </p>
        </div>

        {/* Products Grid - Improved Responsive */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 md:gap-6">
          {filteredProducts.map((product) => (
            <ProductCard
              key={product.id}
              product={product}
              addToCart={addToCart}
              setSelectedProduct={setSelectedProduct}
            />
          ))}
        </div>

        {/* Empty State */}
        {filteredProducts.length === 0 && (
          <div className="text-center py-12">
            <div className="text-4xl md:text-6xl mb-4">üîç</div>
            <h3 className="text-xl md:text-2xl font-bold text-gray-900 mb-2">Produk Tidak Ditemukan</h3>
            <p className="text-gray-600 mb-6 px-4">
              Coba ubah kata kunci pencarian atau kategori
            </p>
            <button
              onClick={() => {
                setSearchTerm('');
                setSelectedCategory('all');
              }}
              className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105"
            >
              Reset Filter
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductsPage;
