// ===========================================
// HOME PAGE COMPONENT - IMPROVED
// ===========================================
import React, { useEffect } from 'react';
import { Star, Truck, CreditCard } from 'lucide-react';

// Import gambar untuk kategori
import marvelImage from '../Image/Marvel.jpeg';
import dcImage from '../Image/DC.jpeg';
import animeImage from '../Image/Anime.jpeg';
import kamenRiderImage from '../Image/Kamen Rider Series.jpeg';
import superSentaiImage from '../Image/Super Sentai.jpeg';

const HomePage = ({ setCurrentPage, setSelectedCategory }) => {
  useEffect(() => {
    // Load model-viewer script only after component mounts
    const loadModelViewer = () => {
      if (!document.querySelector('script[src*="model-viewer"]')) {
        const script = document.createElement('script');
        script.type = 'module';
        script.src = 'https://ajax.googleapis.com/ajax/libs/model-viewer/4.0.0/model-viewer.min.js';
        script.loading = 'lazy';
        document.head.appendChild(script);
      }
    };

    // Delay loading of 3D model to improve LCP
    const timer = setTimeout(loadModelViewer, 1000);

    return () => {
      clearTimeout(timer);
    };
  }, []);

  return (
    <div className="min-h-screen">
      {/* Hero Section dengan optimized loading */}
      <section className="relative bg-gradient-to-br from-blue-900 via-purple-900 to-indigo-900 text-white overflow-hidden">
        <div className="absolute inset-0 bg-black bg-opacity-30"></div>
        <div className="relative container mx-auto px-4 py-12 md:py-20">
          <div className="grid lg:grid-cols-2 gap-8 lg:gap-12 items-center">
            <div className="space-y-4 md:space-y-6 text-center lg:text-left">
              <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold leading-tight">
                Koleksi Action Figure 
                <span className="block bg-gradient-to-r from-yellow-400 to-orange-500 bg-clip-text text-transparent">
                  Terbaik
                </span>
              </h1>
              <p className="text-lg md:text-xl text-gray-300 max-w-2xl mx-auto lg:mx-0">
                Temukan action figure favorit dari Marvel, DC, Anime, Kamen Rider, dan Super Sentai dengan kualitas premium dan harga terbaik.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start">
                <button
                  onClick={() => setCurrentPage('products')}
                  className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 md:px-8 py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105"
                >
                  Belanja Sekarang
                </button>
                <button
                  onClick={() => setCurrentPage('about')}
                  className="border-2 border-white text-white px-6 md:px-8 py-3 rounded-lg font-semibold hover:bg-white hover:text-gray-900 transition-all duration-300"
                >
                  Pelajari Lebih Lanjut
                </button>
              </div>
            </div>
            
            {/* 3D Model Viewer - Lazy loaded */}
            <div className="relative order-first lg:order-last">
              <div className="w-full h-64 md:h-80 lg:h-96 bg-gradient-to-br from-blue-600 to-purple-600 rounded-2xl shadow-2xl flex items-center justify-center">
                <div className="text-center text-white">
                  <div className="text-4xl md:text-6xl mb-4">🤖</div>
                  <p className="text-lg md:text-xl font-semibold">3D Model Loading...</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-12 md:py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12 md:mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Mengapa Memilih Kami?
            </h2>
            <p className="text-lg md:text-xl text-gray-600 max-w-3xl mx-auto">
              Kami menyediakan action figure berkualitas tinggi dengan pelayanan terbaik
            </p>
          </div>
          
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
            <div className="bg-white p-6 md:p-8 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow duration-300">
              <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-6">
                <Star className="text-white" size={32} />
              </div>
              <h3 className="text-xl md:text-2xl font-bold text-gray-900 mb-4">Kualitas Premium</h3>
              <p className="text-gray-600">
                Semua produk action figure kami adalah original dan berkualitas tinggi dari manufacturer resmi.
              </p>
            </div>
            
            <div className="bg-white p-6 md:p-8 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow duration-300">
              <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-blue-600 rounded-full flex items-center justify-center mx-auto mb-6">
                <Truck className="text-white" size={32} />
              </div>
              <h3 className="text-xl md:text-2xl font-bold text-gray-900 mb-4">Pengiriman Cepat</h3>
              <p className="text-gray-600">
                Pengiriman dengan GO-Send untuk Jabodetabek dan JNE untuk seluruh Indonesia.
              </p>
            </div>
            
            <div className="bg-white p-6 md:p-8 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow duration-300 sm:col-span-2 lg:col-span-1">
              <div className="w-16 h-16 bg-gradient-to-r from-yellow-500 to-red-600 rounded-full flex items-center justify-center mx-auto mb-6">
                <CreditCard className="text-white" size={32} />
              </div>
              <h3 className="text-xl md:text-2xl font-bold text-gray-900 mb-4">Pembayaran Mudah</h3>
              <p className="text-gray-600">
                Berbagai metode pembayaran: Transfer Bank, E-wallet (GoPay, OVO, DANA, ShopeePay).
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Popular Categories - Improved Responsive Layout */}
      <section className="py-12 md:py-20 bg-white">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12 md:mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Kategori Populer
            </h2>
            <p className="text-lg md:text-xl text-gray-600 max-w-3xl mx-auto">
              Pilih dari berbagai kategori action figure favorit
            </p>
          </div>
          
          {/* Mobile: Single column, Tablet: 2 columns, Desktop: 5 columns */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4 md:gap-6">
            {[
              { 
                name: 'Marvel', 
                key: 'marvel',
                image: marvelImage
              },
              { 
                name: 'DC', 
                key: 'dc',
                image: dcImage
              },
              { 
                name: 'Anime', 
                key: 'anime',
                image: animeImage
              },
              { 
                name: 'Kamen Rider', 
                key: 'kamen rider',
                image: kamenRiderImage
              },
              { 
                name: 'Super Sentai', 
                key: 'super sentai',
                image: superSentaiImage
              }
            ].map((category, index) => (
              <div
                key={index}
                onClick={() => {
                  setSelectedCategory(category.key);
                  setCurrentPage('products');
                }}
                className="relative overflow-hidden rounded-xl cursor-pointer hover:scale-105 transition-all duration-300 shadow-lg group border-2 border-gray-200 hover:border-gray-300 bg-white"
              >
                {/* Image Container with proper aspect ratio */}
                <div className="relative w-full" style={{ paddingBottom: '75%' }}>
                  <img
                    src={category.image}
                    alt={category.name}
                    className="absolute inset-0 w-full h-full object-contain p-3"
                    loading="lazy"
                  />
                  
                  {/* Hover overlay */}
                  <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-10 transition-opacity duration-300"></div>
                </div>
                
                {/* Category Name */}
                <div className="p-3 bg-white">
                  <h3 className="text-sm md:text-base font-bold text-gray-900 text-center">{category.name}</h3>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
